import React from "react";
import { CiCircleRemove } from "react-icons/ci"
import { GrFormEdit } from "react-icons/gr"
export default function taskitem(){

    return(
        <div style={{backgroundColor:"grey",display:"flex",flexDirection:"row",width:"60vw",}}>
            <p className="item">sl no</p>
            <p className="item" style={{margin:"10px 100px 10px 100px"}} >Task name</p>
            <button classname="item" style={{border:"1px solid blue",color:"blue",margin:"10px 40px 10px 40px"}} value="Todo" />
            <button className="item"><GrFormEdit/></button>
            <button className="item"><CiCircleRemove/></button>
        </div>
    )
}
